mapper.select:
select ort.rn, ort.spotMateNo, ort.startDate, ort.endDate, ort.type, ort.smPeople, ort.point, ort.userNo, ort.id, ort.userName, ort.carNo, ort.carNumber, ort.sPlaceNo, ort.sPlace, ort.sWayNo, ort.sSepPlace, ort.sLat, ort.sLng, ort.sTime, ort.eMateNo, ort.ePlaceNo, ort.ePlace, ort.eWayNo, ort.eSepPlace, ort.eLat, ort.eLng, ort.opts from (select rownum rn, ot.spotMateNo, ot.startDate, ot.endDate, ot.type, ot.smPeople, ot.point, ot.userNo, ot.id, ot.userName, ot.carNo, ot.carNumber, ot.sPlaceNo, ot.sPlace, ot.sWayNo, ot.sSepPlace, ot.sLat, ot.sLng, ot.sTime, ot.eMateNo, ot.ePlaceNo, ot.ePlace, ot.eWayNo, ot.eSepPlace, ot.eLat, ot.eLng, ot.opts from (select sm.no spotMateNo, to_char(sm.startDate, 'YYYY-MM-DD') startDate, to_char(sm.endDate, 'YYYY-MM-DD') endDate, sm.type, sm.people smPeople, sm.point, us.no userNo, us.id, us.name userName, ca.no carNo, ca.carNo carNumber, se.sPlaceNo, se.sPlace, se.sWayNo, se.sSepPlace, se.sLat, se.sLng, se.sTime, se.eMateNo, se.ePlaceNo, se.ePlace, se.eWayNo, se.eSepPlace, se.eLat, se.eLng, pt.opts from spotMate sm, car ca, users us, (select sMateNo, sPlaceNo, sPlace, sWayNo, sSepPlace, sLat, sLng, sTime, eMateNo, ePlaceNo, ePlace, eWayNo, eSepPlace, eLat, eLng from (select p.mateNo sMateNo, p.no sPlaceNo, p.place sPlace, p.wayNo sWayNo, p.sepPlace sSepPlace, p.lat sLat, p.lng sLng, p.time sTime from place p where p.wayNo = -1) s, (select p.mateNo eMateNo, p.no ePlaceNo, p.place ePlace, p.wayNo eWayNo, p.sepPlace eSepPlace, p.lat eLat, p.lng eLng from place p where p.wayNo = 0) e where s.sMateNo = e.eMateNo) se, (select mateNo, LISTAGG(detailNo, ',') within group (order by detailNo asc) opts from spotDetail group by mateNo) pt where sm.no = se.sMateNo and ca.no = sm.carNo and ca.userNo = us.no and (sm.type = 'carpool' or sm.type = 'seasonTicket') and sm.no = pt.mateNo(+) and sm.people > 0
and se.sPlace like '%'||#{splace}||'%'
and se.ePlace like '%'||#{eplace}||'%'
and se.sTime = #{sTime}
and sm.people = #{smPeople}
and to_char(sm.startDate, 'YYYY-MM-DD') >= #{startDate}
and to_char(sm.endDate, 'YYYY-MM-DD') <= #{endDate}
and pt.opts like '%'||#{item}||'%'
order by sm.no desc )ot )ort where rn>=#{startRnum} and rn<=#{endRnum}

select count(*) totCnt from spotMate sm, car ca, users us, (select sMateNo, sPlaceNo, sPlace, sWayNo, sSepPlace, sLat, sLng, sTime, eMateNo, ePlaceNo, ePlace, eWayNo, eSepPlace, eLat, eLng from (select p.mateNo sMateNo, p.no sPlaceNo, p.place sPlace, p.wayNo sWayNo, p.sepPlace sSepPlace, p.lat sLat, p.lng sLng, p.time sTime from place p where p.wayNo = -1) s, (select p.mateNo eMateNo, p.no ePlaceNo, p.place ePlace, p.wayNo eWayNo, p.sepPlace eSepPlace, p.lat eLat, p.lng eLng from place p where p.wayNo = 0) e where s.sMateNo = e.eMateNo) se, (select mateNo, LISTAGG(detailNo, ',') within group (order by detailNo asc) opts from spotDetail group by mateNo) pt where sm.no = se.sMateNo and ca.no = sm.carNo and ca.userNo = us.no and (sm.type = 'carpool' or sm.type = 'seasonTicket') and sm.no = pt.mateNo(+) and sm.people > 0
and se.sPlace like '%'||#{splace}||'%'
and se.ePlace like '%'||#{eplace}||'%'
and se.sTime = #{sTime}
and sm.people = #{smPeople}
and to_char(sm.startDate, 'YYYY-MM-DD') >= #{startDate}
and to_char(sm.endDate, 'YYYY-MM-DD') <= #{endDate}
and pt.opts like '%'||#{item}||'%'

select people
from spotmate
where no = #{mateNo}

mapper.insert:
      
      update spotmate
      set people = people - #{people}
      where no = #{mateNo}

			
