mapper.update:
update notice
set hit = hit+1
where no = #{noticeNo}

mapper.select:
select count(*) from notice where regDate >= to_date(#{startDate}, 'yyyy-mm-dd') -1 and regDate <= to_date(#{endDate}, 'yyyy-mm-dd') +1
and type = #{noti_option}
and title like '%'||#{noticeSearch}||'%'

select rn, no, type, title, hit, regDate, admin, manage, content from(select rownum rn, no, type, title, hit, regDate, admin, manage, content from (select no, type, title, hit, to_char(regDate, 'yyyy-mm-dd') regDate, admin, manage, content from notice where regDate >= to_date(#{nsVo.startDate}, 'yyyy-mm-dd') -1 and regDate <= to_date(#{nsVo.endDate}, 'yyyy-mm-dd') +1
and type = #{nsVo.noti_option}
and title like '%'||#{nsVo.noticeSearch}||'%'
order by no desc)) where rn >= #{sNum} and rn <= #{eNum}
